import React from 'react'
import {Switch, Route} from 'react-router-dom'
import {makeStyles} from '@material-ui/core/styles'
import Box from '@material-ui/core/Box'
import Card from '@material-ui/core/Card'
import IndexCard from './index-card'
import SettingsCard from './settings-card'

const useStyles = makeStyles(theme => ({
    fontStyle: {
        color: '#9ee2ff'
    },
    midItem: {
        marginTop: '1rem',
        marginBottom: '6rem'
    },
    item: {
        flex: 1,
        display: 'flex',
        alignItems: 'center'
    },
    coverLeft: {
        background: 'linear-gradient(to bottom, #307AFF, 50%, #46cdff)',
        alignItems: 'center',
        flex: 1,
        display: 'flex',
        flexDirection: 'column'
    },
    coverContent: {
        display: 'flex',
        justifyContent: 'center',
        flexDirection: 'column',
        color: '#fff',
        position: 'relative'
    },
    coverRight: {
        position: 'relative',
        flex: 1,
        display: 'flex'
    },
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    card: {
        display: 'flex',
        minWidth: 700,
        borderRadius: '10px',
        padding: '10px',
        boxShadow: '0px 6px 18px 0px rgba(0,0,0,0.2)'
    },
    input: {
        maxWidth: '250px',
        minWidth: '250px',
        alignSelf: 'center'
    },
    grid: {
        margin: '0 !important'
    },
    button: {
        height: '44px',
        width: '260px',
        '&:hover': {
            backgroundColor: '#307AFF'
        },
        margin: theme.spacing(1),
        marginTop: '33px',
        backgroundColor: '#44a2fc',
        borderRadius: '30px'
    }
}))

export default function CardPage() {
    const classes = useStyles()

    return (
        <Box display="flex" alignItems="center" justifyContent="center">
            <Card className={classes.card}>
                <Box display="flex" flex="1">
                    <div className={classes.coverLeft}>
                        <div className={classes.item}>
                            <div className='cover-image'/>
                        </div>
                        <div className={classes.item}>
                            <div className={classes.coverContent}>
                                <Box textAlign="center" fontSize="h6.fontSize" className={classes.fontStyle}>Welcome
                                    to</Box>
                                <Box textAlign="center" fontWeight="fontWeightRegular" fontSize="h4.fontSize"
                                     className={classes.midItem}>WEALTH MANAGEMENT</Box>
                                {/* <Box textAlign="center" fontWeight="fontWeightRegular" className={classes.fontStyle} style={{color: "white", fontSize: "17px"}}>Agora Web SDK: {AgoraRTC.VERSION}</Box> */}
                                <Box textAlign="center" fontWeight="fontWeightRegular" className={classes.fontStyle}
                                     fontSize="h7.fontSize">Powered by GENSI</Box>
                            </div>
                        </div>
                    </div>
                    <div className={classes.coverRight}>
                        <Switch>
                            <Route exact path="/" component={IndexCard}></Route>
                            <Route path="/setting" component={SettingsCard}></Route>
                        </Switch>
                    </div>
                </Box>
            </Card>
        </Box>
    )
}
